//data array to file function (will log that the file was written)

let tempString = "Hello this is a file write test";

exports.write = function(sigPadData,filename){
    //if code is 1 then write to client file
    //else write to spouse file
    fs.writeFileSync(filename, sigPadData ,'utf8', (err) => {
        if (err) throw err;
        console.log('signature saved!');
    });
};

//file to data array function (returns data array)

exports.read = function(req,filename){
    //if code is 1 read the client file
    //else read the spouse file
    req.body.sigHolder = fs.readFileSync(filename, 'utf8');
    console.log("string read");
    return(req);
};
